CustomField.formula:
/* Sales Area */
SUBSTITUTE(
	TRIM(
		/* 1. Organization - LSU */
		IF(NOT(ISBLANK(OrganizationAttribute__c )),
			OrganizationAttribute__c ,'')
		/* 2. Country - LSU_FI */
		+IF(AND(NOT(ISBLANK(CountryAttribute__c )),NOT(ISBLANK(OrganizationAttribute__c ))),
			'_'+CountryAttribute__c ,'')
		+IF(AND(NOT(ISBLANK(CountryAttribute__c )),ISBLANK(OrganizationAttribute__c )),
			CountryAttribute__c ,'')
		/* 3. District/Sales Area - LSU_FI_South */
		+IF(AND(NOT(ISBLANK(DistrictAttribute__c )),NOT(ISBLANK(CountryAttribute__c )),ISBLANK(ProfitCenterAttribute__c )),
			'_'+ SUBSTITUTE(DistrictAttribute__c , CountryAttribute__c+'_', '') ,'')
		/* 4. Profit Center - LSU_FI_402 */
		+IF(NOT(ISBLANK(ProfitCenterAttribute__c )),
			'_'+ProfitCenterAttribute__c ,'')
		/* 5. Office - LSU_FI_402_12345 */
		+IF(NOT(ISBLANK(OfficeAttribute__c )),
			'_'+SUBSTITUTE(OfficeAttribute__c , CountryAttribute__c+'_', ''),'')
		/* 6. Business Type - LSU_FI_402_12345_1 */
		+IF(AND(NOT(ISBLANK( BusinessTypeAttribute__c )),ISBLANK(CustomerSegmentAttribute__c )),
			'_'+BusinessTypeAttribute__c ,'')
		/* 7. Customer Segment - LSU_FI_402_12345_1100 */
		+IF(NOT(ISBLANK(CustomerSegmentAttribute__c )),
			'_'+CustomerSegmentAttribute__c ,'')
	),
' ', '_')
